;;
; The game's entrypoint.
; @addr{0150}
begin:
	nop
	di
	cp $11
	ld a,$00
	jr nz,+

	; Check GBA Mode
	inc a
	bit 0,b
	jr z,+
	ld a,$ff
+
	ldh (<hGameboyType),a
	ld a,$37
	ldh (<hRng1),a
	ld a,$0d
	ldh (<hRng2),a
resetGame:
	ld sp,wMainStackTop
	jpfrombank0 init


;;
; Get the number of set bits in a.
;
; @param	a	Value to check
; @param[out]	a,b	Number of set bits in 'a'
; @addr{0176}
getNumSetBits:
	ld b,$00		; $0176
-
	add a			; $0178
	jr nc,+
	inc b			; $017b
+
	or a			; $017c
	jr nz,-
	ld a,b			; $017f
	ret			; $0180

;;
; Add a bcd-encoded number to a 16-bit memory address. If it would go above $9999, the
; result is $9999.
;
; @param[in]  bc	Number to add.
; @param[in]  hl	Address to add with and store result into.
; @param[out] cflag	Set if the value would have gone over $9999.
; @addr{0181}
addDecimalToHlRef:
	ld a,(hl)		; $0181
	add c			; $0182
	daa			; $0183
	ldi (hl),a		; $0184
	ld a,(hl)		; $0185
	adc b			; $0186
	daa			; $0187
	ldd (hl),a		; $0188
	ret nc			; $0189
	ld a,$63		; $018a
	ldi (hl),a		; $018c
	ldd (hl),a		; $018d
	ret			; $018e

;;
; Subtract a bcd-encoded number from a 16-bit memory address. If it would go below 0, the
; result is 0.
;
; @param	bc	Value to subtract.
; @param	hl	Address to subtract with and store result into.
; @param[out]	cflag	Set if the value would have gone under $0000.
; @addr{018f}
subDecimalFromHlRef:
	ld a,(hl)		; $018f
	sub c			; $0190
	daa			; $0191
	ldi (hl),a		; $0192
	ld a,(hl)		; $0193
	sbc b			; $0194
	daa			; $0195
	ldd (hl),a		; $0196
	ret nc			; $0197
	xor a			; $0198
	ldi (hl),a		; $0199
	ldd (hl),a		; $019a
	scf			; $019b
	ret			; $019c

;;
; @param	a	Operand 1
; @param	c	Operand 2
; @param[out]	hl	Result
; @trashes{b,e}
; @addr{019d}
multiplyAByC:
	ld e,$08		; $019d
	ld b,$00		; $019f
	ld l,b			; $01a1
	ld h,b			; $01a2
-
	add hl,hl		; $01a3
	add a			; $01a4
	jr nc,+
	add hl,bc		; $01a7
+
	dec e			; $01a8
	jr nz,-
	ret			; $01ab

;;
; Multiply 'a' by $10, store result in bc.
;
; @param	a	Value to multiply
; @param[out]	bc	Result
; @addr{01ac}
multiplyABy16:
	swap a			; $01ac
	ld b,a			; $01ae
	and $f0			; $01af
	ld c,a			; $01b1
	ld a,b			; $01b2
	and $0f			; $01b3
	ld b,a			; $01b5
	ret			; $01b6

;;
; Multiply 'a' by 8, store result in bc.
;
; @param	a	Value to multiply
; @param[out]	bc	Result
; @addr{01b7}
multiplyABy8:
	swap a			; $01b7
	rrca			; $01b9
	ld b,a			; $01ba
	and $f8			; $01bb
	ld c,a			; $01bd
	ld a,b			; $01be
	and $07			; $01bf
	ld b,a			; $01c1
	ret			; $01c2

;;
; Multiply 'a' by 4, store result in bc.
;
; @param	a	Value to multiply
; @param[out]	bc	Result
; @addr{01c3}
multiplyABy4:
	ld b,$00		; $01c3
	add a			; $01c5
	rl b			; $01c6
	add a			; $01c8
	rl b			; $01c9
	ld c,a			; $01cb
	ret			; $01cc

;;
; Convert a signed 8-bit value in 'a' to signed 16-bit value in 'bc'
;
; @param	a	Signed value
; @param[out]	bc	Signed 16-bit value
; @addr{01cd}
s8ToS16:
	ld b,$ff		; $01cd
	bit 7,a			; $01cf
	jr nz,+
	inc b			; $01d3
+
	ld c,a			; $01d4
	ret			; $01d5

;;
; @param[out]	a	$ff if hl < bc, $01 if hl > bc, $00 if equal
; @addr{01d6}
compareHlToBc:
	ld a,h			; $01d6
	cp b			; $01d7
	jr c,+
	jr nz,++
	ld a,l			; $01dc
	cp c			; $01dd
	jr c,+
	jr nz,++
	xor a			; $01e2
	ret			; $01e3
+
	ld a,$ff		; $01e4
	ret			; $01e6
++
	ld a,$01		; $01e7
	ret			; $01e9

;;
; This returns the highest set bit in 'a', which in effect is like log base 2.
;
; @param[out]	a	Bit value (0-7) or unchanged if no bits are set
; @param[out]	cflag	c if at least one bit was set (output is valid)
; @addr{01ea}
getHighestSetBit:
	or a			; $01ea
	ret z			; $01eb
	push bc			; $01ec
	ld c,$ff		; $01ed
-
	inc c			; $01ef
	srl a			; $01f0
	jr nz,-
	ld a,c			; $01f4
	pop bc			; $01f5
	scf			; $01f6
	ret			; $01f7

;;
; @param[out]	a	Bit value (0-7) or unchanged if no bits are set
; @param[out]	cflag	c if at least one bit was set (output is valid)
; @addr{01f8}
getLowestSetBit:
	or a			; $01f8
	ret z			; $01f9
	push bc			; $01fa
	ld c,$08		; $01fb
-
	dec c			; $01fd
	add a			; $01fe
	jr nz,-
	ld a,c			; $0201
	pop bc			; $0202
	scf			; $0203
	ret			; $0204

;;
; A "flag" is just a bit in memory. These flag-related functions take a base address
; ('hl'), and check a bit in memory starting at that address ('a').
;
; @param	a	Flag to check
; @param	hl	Start address of flags
; @param[out]	zflag	Set if the flag is not set.
; @addr{0205}
checkFlag:
	push hl			; $0205
	push bc			; $0206
	call _flagHlpr		; $0207
	and (hl)		; $020a
	pop bc			; $020b
	pop hl			; $020c
	ret			; $020d

;;
; @param	a	Flag to set
; @param	hl	Start address of flags
; @addr{020e}
setFlag:
	push hl			; $020e
	push bc			; $020f
	call _flagHlpr		; $0210
	or (hl)			; $0213
	ld (hl),a		; $0214
	pop bc			; $0215
	pop hl			; $0216
	ret			; $0217

;;
; @param	a	Flag to unset
; @param	hl	Start address of flags
; @addr{0218}
unsetFlag:
	push hl			; $0218
	push bc			; $0219
	call _flagHlpr		; $021a
	cpl			; $021d
	and (hl)		; $021e
	ld (hl),a		; $021f
	pop bc			; $0220
	pop hl			; $0221
	ret			; $0222

;;
; Add (a/8) to hl, set 'a' to a bitmask for the desired bit (a%8)
;
; @addr{0223}
_flagHlpr:
	ld b,a			; $0223
	and $f8			; $0224
	rlca			; $0226
	swap a			; $0227
	ld c,a			; $0229
	ld a,b			; $022a
	ld b,$00		; $022b
	add hl,bc		; $022d
	and $07			; $022e
	ld bc,bitTable		; $0230
	add c			; $0233
	ld c,a			; $0234
	ld a,(bc)		; $0235
	ret			; $0236

;;
; @addr{0237}
decHlRef16WithCap:
	inc hl			; $0237
	ldd a,(hl)		; $0238
	or (hl)			; $0239
	ret z			; $023a
	ld a,(hl)		; $023b
	sub $01			; $023c
	ldi (hl),a		; $023e
	ld a,(hl)		; $023f
	sbc $00			; $0240
	ldd (hl),a		; $0242
	or (hl)			; $0243
	ret			; $0244

;;
; @addr{0245}
incHlRefWithCap:
	inc (hl)		; $0245
	ret nz			; $0246
	ld (hl),$ff		; $0247
	ret			; $0249

;;
; @addr{024a}
incHlRef16WithCap:
	inc (hl)		; $024a
	ret nz			; $024b
	inc hl			; $024c
	inc (hl)		; $024d
	jr z,+
	dec hl			; $0250
	ret			; $0251
+
	push af			; $0252
	ld a,$ff		; $0253
	ldd (hl),a		; $0255
	ld (hl),a		; $0256
	pop af			; $0257
	ret			; $0258

;;
; Convert hex value in a to a bcd-encoded decimal value in bc
;
; @param	a	Hexadecimal number
; @param[out]	bc	bcd-encoded decimal number
; @addr{0259}
hexToDec:
	ld bc,$0000		; $0259
-
	cp 100			; $025c
	jr c,@doneHundreds	; $025e
	sub 100			; $0260
	inc b			; $0262
	jr -

@doneHundreds:
	cp 10			; $0265
	ret c			; $0267
	sub 10			; $0268
	inc c			; $026a
	jr @doneHundreds		; $026b

;;
; Update wKeysPressed, wKeysJustPressed, and wKeysPressedLastFrame.
;
; @trashes{bc,hl}
; @addr{026d}
pollInput:
	ld c,R_P1		; $026d
	ld a,$20		; $026f
	ld ($ff00+c),a		; $0271
	ld a,($ff00+c)		; $0272
	ld a,($ff00+c)		; $0273
	ld a,($ff00+c)		; $0274
	ld b,a			; $0275
	ld a,$10		; $0276
	ld ($ff00+c),a		; $0278
	ld a,b			; $0279
	and $0f			; $027a
	swap a			; $027c
	ld b,a			; $027e
	ld hl,wKeysPressed
	ldd a,(hl)		; $0282
	ldi (hl),a		; $0283
	cpl			; $0284
	ld (hl),a		; $0285
	ld a,($ff00+c)		; $0286
	ld a,($ff00+c)		; $0287
	and $0f			; $0288
	or b			; $028a
	cpl			; $028b
	ld b,(hl)		; $028c
	ldi (hl),a		; $028d
	and b			; $028e
	ld (hl),a		; $028f
	ld a,$30		; $0290
	ld ($ff00+c),a		; $0292
	ret			; $0293

;;
; @addr{0294}
getInputWithAutofire:
	push hl			; $0294
	push bc			; $0295
	ld a,(wKeysPressed)		; $0296
	and $f0			; $0299
	ld b,a			; $029b
	ld hl,wAutoFireKeysPressed
	ld a,(hl)		; $029f
	and b			; $02a0
	ld a,b			; $02a1
	ldi (hl),a		; $02a2
	jr z,+
	inc (hl)		; $02a5
	ld a,(hl)		; $02a6
	cp $28			; $02a7
	jr c,++
	and $1f			; $02ab
	or $80			; $02ad
	ld (hl),a		; $02af
	and $03			; $02b0
	jr nz,++
	ld a,(wKeysPressed)
	jr +++
+
	xor a			; $02b9
	ld (hl),a		; $02ba
++
	ld a,(wKeysJustPressed)		; $02bb
+++
	pop bc			; $02be
	pop hl			; $02bf
	ret			; $02c0

;;
; @addr{02c1}
disableLcd:
	ld a,($ff00+R_LCDC)	; $02c1
	rlca			; $02c3
	ret nc			; $02c4
	push bc			; $02c5
	ld a,($ff00+R_IE)	; $02c6
	ld b,a			; $02c8
	and INT_VBLANK ~ $FF
	ld ($ff00+R_IE),a	; $02cb
-
	ld a,($ff00+R_LY)	; $02cd
	cp $91			; $02cf
	jr c,-
	ld a,$03		; $02d3
	ldh (<hNextLcdInterruptBehaviour),a	; $02d5
	xor a			; $02d7
	ld (wGfxRegsFinal.LCDC),a		; $02d8
	ld (wGfxRegs2.LCDC),a		; $02db
	ld (wGfxRegs1.LCDC),a		; $02de
	ld ($ff00+R_LCDC),a	; $02e1
	ld ($ff00+R_IF),a	; $02e3
	ld a,b			; $02e5
	ld ($ff00+R_IE),a	; $02e6
	pop bc			; $02e8
	ret			; $02e9

;;
; @param	a	Gfx register state index to load
; @addr{02ea}
loadGfxRegisterStateIndex:
	; a *= $06
	ld l,a			; $02ea
	add a			; $02eb
	add l			; $02ec
	add a			; $02ed

	ld hl,gfxRegisterStates
	rst_addDoubleIndex			; $02f1
	ld b,GfxRegsStruct.size*2
	ld de,wGfxRegs1		; $02f4
-
	ldi a,(hl)		; $02f7
	ld (de),a		; $02f8
	inc e			; $02f9
	dec b			; $02fa
	jr nz,-
	ld a,(wGfxRegs1.LCDC)		; $02fd
	ld (wGfxRegsFinal.LCDC),a		; $0300
	ld ($ff00+R_LCDC),a	; $0303
	ret			; $0305

