	ldi (hl),a		; $05a5
	ld a,e			; $05a6
	ldi (hl),a		; $05a7
	ld a,b			; $05a8
	ldi (hl),a		; $05a9
	ld a,l			; $05aa
	ldh (<hVBlankFunctionQueueTail),a	; $05ab
	scf			; $05ad
	ret			; $05ae
++
; If LCD is off, copy data immediately?
	ldh a,(<hRomBank)	; $05af
	push af			; $05b1
	ld a,($ff00+R_SVBK)	; $05b2
	push af			; $05b4
	push de			; $05b5
	push hl			; $05b6
	ld a,c			; $05b7
