	setrombank		; $05ba
	pop de			; $05bf
	ld hl, HDMA1
	ld (hl),d		; $05c3
	inc l			; $05c4
	ld (hl),e		; $05c5
	inc l			; $05c6
	pop de			; $05c7
	ld a,e			; $05c8
	ld ($ff00+R_VBK),a	; $05c9
	ld (hl),d		; $05cb
	inc l			; $05cc
	ldi (hl),a		; $05cd
	ld (hl),b		; $05ce
	pop af			; $05cf
	ld ($ff00+R_SVBK),a	; $05d0
	pop af			; $05d2
