	inc l			; $148e
	inc l			; $148f
	ld c,(hl)		; $1490
	ld a,b			; $1491
	and $f0			; $1492
	ld l,a			; $1494
	ld a,c			; $1495
	swap a			; $1496
	and $0f			; $1498
	or l			; $149a
	ld l,a			; $149b

	ld h,>wRoomCollisions
	ld a,(hl)		; $149e

