; Sets carry flag if the object is not in a wall?
_simpleCollision:
	bit 3,b			; $1545
	jr nz,+
	rrca			; $1549
	rrca			; $154a
+
	bit 3,c			; $154b
	jr nz,+
	rrca			; $154f
+
	rrca			; $1550
	ret			; $1551

