; @param	hl
; @param[out]	cflag	Set on collision?
; @param[out]	zflag	Unset on collision?
_complexCollision:
	push de			; $1552
	and $0f			; $1553
	ld e,a			; $1555
	ld d,$00		; $1556

	add hl,de		; $1558
	ld e,(hl)		; $1559
	cp $08			; $155a
	ld a,b			; $155c
	jr nc,+
	ld a,c			; $155f
+
	rrca			; $1560
	and $07			; $1561
	ld hl,bitTable		; $1563
	add l			; $1566
	ld l,a			; $1567
	ld a,(hl)		; $1568
	and e			; $1569
	pop de			; $156a
	ret z			; $156b
	scf			; $156c
	ret			; $156d

