	ld a,(wGameKeysJustPressed)		; $1b5d
	and BTN_A			; $1b60
	ret z			; $1b62

	; If he's in a shop, he can interact while holding something
	ld a,(wInShop)		; $1b63
	or a			; $1b66
	jr nz,+			; $1b67

