	push hl			; $1b8e
	ldh a,(<hFF8D)		; $1b8f
	ld b,a			; $1b91
	ldh a,(<hFF8C)		; $1b92
	ld c,a			; $1b94
	call objectHCheckContainsPoint		; $1b95
	pop hl			; $1b98
	jr nc,+			; $1b99

	; Link is next to the object; only trigger it if the "pressedAButton" variable is
	; not already set.
	bit 0,(hl)		; $1b9b
	jr z,@foundObject			; $1b9d
+
	inc e			; $1b9f
	ld a,e			; $1ba0
