; @param	d	Current object
; @param	hFF8E	X
; @param	hFF8F	Y
; @addr{1eb1}
objectGetRelativeAngleWithTempVars:
	ld e,$08		; $1eb1
	ld a,b			; $1eb3
	add e			; $1eb4
	ld b,a			; $1eb5
	ld a,c			; $1eb6
	add e			; $1eb7
	ld c,a			; $1eb8
	ld e,$00		; $1eb9
	ldh a,(<hFF8F)	; $1ebb
	add $08			; $1ebd
	sub b			; $1ebf
	jr nc,+			; $1ec0

	cpl			; $1ec2
	inc a			; $1ec3
	ld e,$04		; $1ec4
+
	ld h,a			; $1ec6
	ldh a,(<hFF8E)	; $1ec7
	add $08			; $1ec9
	sub c			; $1ecb
	jr nc,+			; $1ecc

	cpl			; $1ece
	inc a			; $1ecf
	inc e			; $1ed0
	inc e			; $1ed1
+
	cp h			; $1ed2
	jr nc,+			; $1ed3

	inc e			; $1ed5
	ld l,a			; $1ed6
	ld a,h			; $1ed7
	ld h,l			; $1ed8
+
	ld c,e			; $1ed9
	ld b,$00		; $1eda
	srl a			; $1edc
	srl a			; $1ede
	srl a			; $1ee0
	add a			; $1ee2
	ld l,a			; $1ee3
	cp h			; $1ee4
	jr nc,++		; $1ee5

	inc b			; $1ee7
	add l			; $1ee8
	cp h			; $1ee9
	jr nc,++		; $1eea

	inc b			; $1eec
	add l			; $1eed
	cp h			; $1eee
	jr nc,++		; $1eef

	inc b			; $1ef1
	add l			; $1ef2
	cp h			; $1ef3
	jr nc,++		; $1ef4
	inc b			; $1ef6
++
	ld a,c			; $1ef7
	add a			; $1ef8
	add a			; $1ef9
	add a			; $1efa
	add b			; $1efb
	ld c,a			; $1efc
	ld b,$00		; $1efd
	ld hl,pushDirectionData	; $1eff
	add hl,bc		; $1f02
	ld a,(hl)		; $1f03
	ret			; $1f04

; @addr{1f05}
pushDirectionData:
	.db $18 $19 $1a $1b $1c $00 $00 $00
	.db $00 $1f $1e $1d $1c $00 $00 $00
	.db $08 $07 $06 $05 $04 $00 $00 $00
	.db $00 $01 $02 $03 $04 $00 $00 $00
	.db $18 $17 $16 $15 $14 $00 $00 $00
	.db $10 $11 $12 $13 $14 $00 $00 $00
	.db $08 $09 $0a $0b $0c $00 $00 $00
	.db $10 $0f $0e $0d $0c $00 $00 $00

;;
; @param	a	Z Acceleration
; @param[out]	hl	Object.speedZ variable
; @param[out]	zflag	Set if resulting position is below or on the ground
; @addr{1f45}
objectUpdateSpeedZ:
	ld c,a			; $1f45
;;
; @param	c	Z Acceleration
; @param[out]	hl	Object.speedZ variable
; @param[out]	zflag	Set if resulting position is below or on the ground
; @addr{1f46}
objectUpdateSpeedZ_paramC:
	ldh a,(<hActiveObjectType)	; $1f46
	add Object.z			; $1f48
	ld e,a			; $1f4a
	add Object.speedZ - Object.z			; $1f4b
	ld l,a			; $1f4d
	ld h,d			; $1f4e
	call add16BitRefs		; $1f4f
	bit 7,a			; $1f52
	jr z,@belowGround

; Above ground
	dec l			; $1f56
	ld a,c			; $1f57
	add (hl)		; $1f58
	ldi (hl),a		; $1f59
	ld a,$00		; $1f5a
	adc (hl)		; $1f5c
	ld (hl),a		; $1f5d
	or d			; $1f5e
	ret			; $1f5f

; Can't be below ground, set z position to 0
@belowGround:
	xor a			; $1f60
	ld (de),a		; $1f61
	dec e			; $1f62
	ld (de),a		; $1f63
	xor a			; $1f64
	ret			; $1f65

;;
; Updates an object's speedZ in a way that works with sidescrolling areas.
;
; @param	a	Gravity (amount to add to Object.speedZ)
; @param[out]	cflag	Set if the object has landed.
; @addr{1f66}
objectUpdateSpeedZ_sidescroll:
	ld b,$06		; $1f66

;;
; @param	a	Gravity (amount to add to Object.speedZ)
; @param	b	Y offset for collision check
; @param[out]	cflag	Set if the object has landed.
; @addr{1f68}
objectUpdateSpeedZ_sidescroll_givenYOffset:
	ldh (<hFF8B),a	; $1f68
	ldh a,(<hActiveObjectType)	; $1f6a
	add Object.speedZ+1			; $1f6c
	ld l,a			; $1f6e
	ld h,d			; $1f6f
	bit 7,(hl)		; $1f70
	jr nz,@notLanded	; $1f72

; speedZ is positive; return with carry flag set if the object collides with a tile.

	; Set b to object's y position (plus offset)
	add Object.yh-(Object.speedZ+1)			; $1f74
	ld l,a			; $1f76
	ldi a,(hl)		; $1f77
	add b			; $1f78
	ld b,a			; $1f79

	; hl = Object.xh
	inc l			; $1f7a
	ld a,(hl)		; $1f7b

	; Check left side of object (assumes 8 pixel width)
	sub $04			; $1f7c
	ld c,a			; $1f7e
	call checkTileCollisionAt_allowHoles		; $1f7f
	ret c			; $1f82

	; Check right side of object (assumes 8 pixel width)
	ld a,c			; $1f83
	add $07			; $1f84
	ld c,a			; $1f86
	call checkTileCollisionAt_allowHoles		; $1f87
	ret c			; $1f8a

@notLanded:
	; Add speedZ to y position
	ldh a,(<hActiveObjectType)	; $1f8b
	add Object.y			; $1f8d
	ld e,a			; $1f8f
	add Object.speedZ-Object.y			; $1f90
	ld l,a			; $1f92
	ld h,d			; $1f93
	call add16BitRefs		; $1f94

	; Apply gravity (increase speedZ by amount passed to function)
	dec l			; $1f97
	ldh a,(<hFF8B)	; $1f98
	add (hl)		; $1f9a
	ldi (hl),a		; $1f9b
	ld a,$00		; $1f9c
	adc (hl)		; $1f9e
	ld (hl),a		; $1f9f

	; Clear carry flag
	or d			; $1fa0
	ret			; $1fa1

;;
; Checks if Link is within the distance given to the object (valid area is a square).
;
; Returns the direction Link is in relative to the object, in a slightly different format
; than normal?
;
; @param	c	How close Link must be to the object for pushing to work
; @param[out]	a	Direction Link is in relative to the object? (divide by 2 to get
;			a standard direction value)
; @param[out]	cflag	c if Link is within the specified distance. If unset, 'a' won't
;			be calculated properly.
; @addr{1fa2}
objectCheckLinkWithinDistance:
	ldh a,(<hActiveObjectType)	; $1fa2

	; Get the difference between the object's and link's y positions
	add Object.yh			; $1fa4
	ld l,a			; $1fa6
	ld h,d			; $1fa7
	ld e,$04		; $1fa8
	ld a,(w1Link.yh)		; $1faa
	sub (hl)		; $1fad
	jr nc,+			; $1fae
	cpl			; $1fb0
	inc a			; $1fb1
	ld e,$00		; $1fb2
+
	ld b,a			; $1fb4
	ld a,c			; $1fb5
	sub b			; $1fb6
	ccf			; $1fb7
	ret nc			; $1fb8

	; Get the difference between the object's and link's x positions
	ld c,a			; $1fb9
	inc l			; $1fba
	inc l			; $1fbb
	set 5,e			; $1fbc
	ld a,(w1Link.xh)		; $1fbe
	sub (hl)		; $1fc1
	jr nc,+			; $1fc2

	cpl			; $1fc4
	inc a			; $1fc5
	set 6,e			; $1fc6
+
	cp c			; $1fc8
	ret nc			; $1fc9
	cp b			; $1fca
	jr c,+			; $1fcb
	swap e			; $1fcd
+
	ld a,e			; $1fcf
	and $06			; $1fd0
	scf			; $1fd2
	ret			; $1fd3

;;
; Increments or decrements an object's angle by one unit toward the given value.
;
; @param	a	Angle value to move toward
; @addr{1fd4}
objectNudgeAngleTowards:
	ld c,a			; $1fd4
	ldh a,(<hActiveObjectType)	; $1fd5
	add Object.angle			; $1fd7
	ld e,a			; $1fd9
	ld a,(de)		; $1fda
	ld b,a			; $1fdb

	sub c			; $1fdc
	jr z,++			; $1fdd

	and $1f			; $1fdf
	cp $10			; $1fe1
	jr nc,+			; $1fe3

	dec b			; $1fe5
	jr ++			; $1fe6
+
	inc b			; $1fe8
++
	ld a,b			; $1fe9
	and $1f			; $1fea
	ld (de),a		; $1fec
	ret			; $1fed

;;
; Checks if link is centered within 'b' pixels compared to another object (horizontally or
; vertically).
;
; @param	b	Distance threshold
; @param	d	Object to compare with
; @param[out]	cflag	Set if link is centered within the threshold given.
; @addr{1fee}
objectCheckCenteredWithLink:
	ld c,b			; $1fee
	sla c			; $1fef
	inc c			; $1ff1

	; Check Y
	ld h,d			; $1ff2
	ldh a,(<hActiveObjectType)	; $1ff3
	add Object.yh			; $1ff5
	ld l,a			; $1ff7
	ld a,(w1Link.yh)		; $1ff8
	sub (hl)		; $1ffb
	add b			; $1ffc
	cp c			; $1ffd
	ret c			; $1ffe

	; Check X
	inc l			; $1fff
	inc l			; $2000
	ld a,(w1Link.xh)		; $2001
	sub (hl)		; $2004
	add b			; $2005
	cp c			; $2006
	ret			; $2007

;;
; This function reads Object.speed differently than most places (ie. objectApplySpeed). It
; adds variables $10-$11 to Object.y as a 16-bit value, and $12-$13 to Object.x.
;
; @addr{2008}
objectApplyComponentSpeed:
	ldh a,(<hActiveObjectType)	; $2008
	add Object.y			; $200a
	ld l,a			; $200c
	add Object.speed-Object.y			; $200d
	ld e,a			; $200f
	ld h,d			; $2010
	call @addSpeedComponent		; $2011
	inc e			; $2014

;;
; @addr{2015}
@addSpeedComponent:
	ld a,(de)		; $2015
	add (hl)		; $2016
	ldi (hl),a		; $2017
	inc e			; $2018
	ld a,(de)		; $2019
	adc (hl)		; $201a
	ldi (hl),a		; $201b
	ret			; $201c

;;
; Uses the object's speed and angle variables to update its position.
;
; @param[out]	a	New value of object.xh
; @addr{201d}
objectApplySpeed:
	ld h,d			; $201d
	ldh a,(<hActiveObjectType)	; $201e
	add Object.angle		; $2020
	ld e,a			; $2022
	ld l,a			; $2023
	ld c,(hl)		; $2024
	add Object.speed-Object.angle		; $2025
	ld l,a			; $2027
	ld b,(hl)		; $2028

;;
; @param	b	speed value
; @param	c	angle value
; @param	de	Address of an object's angle variable (will only read/write the
;			Y and X values which follow that, not the angle itself).
; @param[out]	a	New value of object.xh
; @addr{2029}
objectApplyGivenSpeed:
	call getPositionOffsetForVelocity		; $2029
	ret z			; $202c

	; Add to Object.y
	inc e			; $202d
	ld a,(de)		; $202e
	add (hl)		; $202f
	ld (de),a		; $2030
	inc e			; $2031
	inc l			; $2032
	ld a,(de)		; $2033
	adc (hl)		; $2034
	ld (de),a		; $2035

	; Add to Object.x
	inc e			; $2036
	inc l			; $2037
	ld a,(de)		; $2038
	add (hl)		; $2039
	ld (de),a		; $203a
	inc e			; $203b
	inc l			; $203c
	ld a,(de)		; $203d
	adc (hl)		; $203e
	ld (de),a		; $203f
	ret			; $2040

;;
; Takes a speed and an angle, and calculates the values to add to an object's y and
; x positions.
;
; @param	b	speed (should be a multiple of 5)
; @param	c	angle (value from $00-$1f)
; @param[out]	hl	Pointer to 4 bytes of data to be added to Y and X positions.
;			It always points to wTmpcec0.
; @param[out]	zflag	Set if the speed / angle was invalid (or speed is zero)
; @addr{2041}
getPositionOffsetForVelocity:
	bit 7,c			; $2041
	jr nz,@invalid		; $2043

	swap b			; $2045
	jr z,@invalid		; $2047

	ld a,b			; $2049
	ld hl,objectSpeedTable-$50		; $204a
	sla c			; $204d
	ld b,$00		; $204f
	add hl,bc		; $2051

	ld b,a			; $2052
	and $f0			; $2053
	ld c,a			; $2055
	ld a,b			; $2056
	and $0f			; $2057
	ld b,a			; $2059
	add hl,bc		; $205a
	ldh a,(<hRomBank)	; $205b
	push af			; $205d
	ld a,:objectSpeedTable		; $205e
	setrombank		; $2060

	; Get Y values
	ld bc,wTmpcec0		; $2065
	ldi a,(hl)		; $2068
	ld (bc),a		; $2069
	inc c			; $206a
	ldi a,(hl)		; $206b
	ld (bc),a		; $206c

	; Get X values
	inc c			; $206d
	ld a,$0e		; $206e
	rst_addAToHl			; $2070

	ldi a,(hl)		; $2071
	ld (bc),a		; $2072
	inc c			; $2073
	ldi a,(hl)		; $2074
	ld (bc),a		; $2075

	pop af			; $2076
	setrombank		; $2077

	ld hl,wTmpcec0		; $207c
	or h			; $207f
	ret			; $2080

@invalid:
	ld hl,wTmpcec0+3		; $2081
	xor a			; $2084
	ldd (hl),a		; $2085
	ldd (hl),a		; $2086
	ldd (hl),a		; $2087
	ld (hl),a		; $2088
	ret			; $2089

;;
; @param[out]	bc	Object's position
