	add Object.yh		; $208c
	ld e,a			; $208e
	ld a,(de)		; $208f
	ld b,a			; $2090
	inc e			; $2091
	inc e			; $2092
	ld a,(de)		; $2093
	ld c,a			; $2094
	ret			; $2095

;;
; @param[out]	a	Object's position (short form)
; @addr{2096}
objectGetShortPosition:
	ldh a,(<hActiveObjectType)	; $2096
	add Object.yh		; $2098
	ld e,a			; $209a
;;
; @addr{209b}
getShortPositionFromDE:
	ld a,(de)		; $209b
--
