;;
; @param	a	Value to add to the object's Y position before calculating
; @param[out]	a	Object's position (short form)
; @addr{20a8}
objectGetShortPosition_withYOffset:
	ld b,a			; $20a8
	ldh a,(<hActiveObjectType)	; $20a9
	add Object.yh			; $20ab
	ld e,a			; $20ad
	ld a,(de)		; $20ae
	add b			; $20af
