	call getScaledPositionOffsetForVelocity		; $214c

	ldh a,(<hActiveObjectType)	; $214f
	or Object.speedX+1			; $2151

	; X speed
	ld e,a			; $2153
	ldd a,(hl)		; $2154
	ld (de),a		; $2155
	dec e			; $2156
	ldd a,(hl)		; $2157
	ld (de),a		; $2158

	; Y speed
	dec e			; $2159
	ldd a,(hl)		; $215a
	ld (de),a		; $215b
	dec e			; $215c
	ld a,(hl)		; $215d
	ld (de),a		; $215e
	ret			; $215f

