;;
; Gets priority based on height relative to link?
;
; @param	d	Object
; @param[out]	b	Priority
; @addr{22ee}
@getPriority:
	ldh a,(<hActiveObjectType)	; $22ee
	add Object.zh			; $22f0
	ld e,a			; $22f2
	ld a,(de)		; $22f3
	; return if Z position is between 1 and 16
