	ret c			; $22f9

	dec b			; $22fa
	ld a,e			; $22fb
	add Object.yh-Object.zh			; $22fc
	ld e,a			; $22fe
	ld a,(de)		; $22ff
	ld e,a			; $2300

	ld a,(wLinkObjectIndex)		; $2301
	ld h,a			; $2304
	ld l,<w1Link.yh		; $2305
	ld a,(hl)		; $2307
