;;
; This function is called for every enemy before calling their regular code. The value of
; 'c' is moved to 'a' before the enemy-specific code is called, so that code can check the
; return value of this function.
;
; @param[out]	c	$00 normally
;			$02 if stunned
;			$03 if health is 0
;			$04 if something
;			$05 if the enemy is experiencing knockback
; @addr{28fd}
enemyFunc28fd:
	ld h,d			; $28fd
	ld l,Enemy.state	; $28fe
	ld a,(hl)		; $2900
	or a			; $2901
	jr z,@uninitialized	; $2902

	ld l,Enemy.var2a		; $2904
	bit 7,(hl)		; $2906
	jr nz,@ret04		; $2908

	ld e,Enemy.knockbackCounter		; $290a
	ld a,(de)		; $290c
	and $7f			; $290d
	jr nz,@knockback	; $290f

	; Enemy.health
	dec l			; $2911
	ld a,(hl)		; $2912
	or a			; $2913
	jr z,@healthZero	; $2914

	; Enemy.stunCounter
	inc e			; $2916
	ld a,(de)		; $2917
	or a			; $2918
	jr nz,@stunned		; $2919

@ret00:
	ld c,$00		; $291b
	ret			; $291d

@uninitialized:
	callab bank3f.enemyLoadGraphicsAndProperties		; $291e
	call getRandomNumber_noPreserveVars		; $2926
	ld e,Enemy.var3d		; $2929
	ld (de),a		; $292b
	inc e			; $292c
	ld a,$01		; $292d
	ld (de),a		; $292f
	jr @ret00		; $2930

@ret04:
	ld c,$04		; $2932
	ret			; $2934

@knockback:
	ld l,e			; $2935
	dec (hl)		; $2936
	ld c,$05		; $2937
	ret			; $2939

@healthZero:
	ld l,Enemy.var3f		; $293a
	bit 1,(hl)		; $293c
	jr nz,@ret00		; $293e
	ld c,$03		; $2940
	ret			; $2942

@stunned:
	ld a,(wFrameCounter)		; $2943
	rrca			; $2946
	jr nc,++		; $2947

	; Decrement Enemy.stunCounter
	ld l,e			; $2949
	dec (hl)		; $294a

	; With 30 frames before being unstunned, make the enemy shake back and forth
	ld a,(hl)		; $294b
	cp 30			; $294c
	jr nc,++		; $294e
	rrca			; $2950
	jr nc,++		; $2951

	ld l,Enemy.xh		; $2953
	ld a,(hl)		; $2955
	xor $01			; $2956
	ld (hl),a		; $2958
++
	; Have the enemy fall down to the ground and bounce

	ld l,Enemy.state		; $2959
	ld a,(hl)		; $295b
	cp $08			; $295c
	jr c,@reachedGround	; $295e

	ld l,Enemy.zh		; $2960
	ld a,(hl)		; $2962
	dec a			; $2963
	cp $08			; $2964
	jr c,@reachedGround	; $2966

	ld c,$20		; $2968
	call objectUpdateSpeedZAndBounce		; $296a
	jr nc,@ret02		; $296d

	ld h,d			; $296f

@reachedGround:
	ld l,Enemy.speedZ		; $2970
	xor a			; $2972
	ldi (hl),a		; $2973
	ld (hl),a		; $2974

@ret02:
	ld c,$02		; $2975
	ret			; $2977

