; @addr{464c}
@state4:
	; Load one entry from the unique gfx per frame
	call updateAreaUniqueGfx		; $464c
	ret c			; $464f

	; Finished loading unique gfx

	ld a,(wAreaUniqueGfx)		; $4650
	ld (wLoadedAreaUniqueGfx),a		; $4653
	xor a			; $4656
	ld (wAreaUniqueGfx),a		; $4657
