	ld c,a			; $47f2
	ld a,(hl)		; $47f3
	cp $ff			; $47f4
	ret z			; $47f6
	ld a,($cd02)		; $47f7
	cp (hl)			; $47fa
	jr nz,+			; $47fb
	ld a,c			; $47fd
	cp b			; $47fe
	jr z,++			; $47ff
+
