;
; @addr{588f}
_paletteThread_mixBG567Palettes:
	ld hl,w2AreaBgPalettes+5*8	; $588f
	ld e,<w2ColorComponentBuffer1+$24		; $5892
	ld b,3*4		; $5894
++
	ld a,:w2AreaBgPalettes		; $5896
	ld ($ff00+R_SVBK),a	; $5898

@nextColor:
	push bc			; $589a
	push hl			; $589b

	; Mix the two colors; result is stored in hl = value<<4, so we still need to
	; extract the final value we want
	call @mixColors		; $589c
	inc e			; $589f
	swap l			; $58a0
	ld a,l			; $58a2
	and $0f			; $58a3
	ld l,a			; $58a5
	ld a,h			; $58a6
	swap a			; $58a7
	or l			; $58a9
	ldh (<hFF8B),a	; $58aa

	; Mix the next component
	call @mixColors		; $58ac
	inc e			; $58af
	swap l			; $58b0
	ld a,l			; $58b2
	and $0f			; $58b3
	ld l,a			; $58b5
	ld a,h			; $58b6
	swap a			; $58b7
	or l			; $58b9
	ldh (<hFF8D),a	; $58ba

	; Mix the next component
	call @mixColors		; $58bc
	inc e			; $58bf
	swap l			; $58c0
	ld a,l			; $58c2
	and $0f			; $58c3
	ld l,a			; $58c5
	ld a,h			; $58c6
	swap a			; $58c7
	or l			; $58c9
	ldh (<hFF8C),a	; $58ca

