;;
; Fills a rectangular area in a tilemap with the given values.
;
; @param	b	Height
; @param	c	Width
; @param	de	Tile index (d) and flag (e) to fill in every position
; @param	hl	Tilemap (top-left position to start at)
; @addr{5d08}
_fillRectangleInTilemap:
	push hl			; $5d08
