;;
; Draws an treasure in the inventory to the background layer.
;
; (This is not for the A/B items, since they use sprites as well as the background layer.
; Those are dealt with in the "updateStatusBar" function.)
;
; @param	de	Where to draw to (a tilemap of some sort)
; @param	hl	Pointer to second byte of treasure display data (ie. wTmpcec0+1)
;			(See the "treasureDisplayData" structures for details on format)
; @param	hFF8B	The number to draw with the treasure (if applicable)
; @addr{5d1c}
_drawTreasureDisplayDataToBg:
	; Draw the left tile
	ldi a,(hl)		; $5d1c
	ld c,a			; $5d1d
	ldi a,(hl)		; $5d1e
	ld b,a			; $5d1f
	call @writeTile		; $5d20

	; Draw the right tile
	inc e			; $5d23
	ldi a,(hl)		; $5d24
	ld c,a			; $5d25
	ldi a,(hl)		; $5d26
	ld b,a			; $5d27
	call @writeTile		; $5d28

	; Draw the "extra tiles" (ammo count, etc)
	ld a,$20		; $5d2b
	call addAToDe		; $5d2d
	ldh a,(<hFF8B)	; $5d30
	ld b,a			; $5d32
	ld c,$07		; $5d33
	ldi a,(hl)		; $5d35
	jp _drawTreasureExtraTiles		; $5d36

;;
; @param bc
; @addr{5d39}
@writeTile:
	push de			; $5d39
	ld a,c			; $5d3a
	or a			; $5d3b
	jr z,@clearTile		; $5d3c

	inc b			; $5d3e
	inc b			; $5d3f
	add a			; $5d40
	jr nc,+			; $5d41
	set 3,b			; $5d43
+
	ld c,a			; $5d45
	call @writeTileHlpr	; $5d46
	pop de			; $5d49
	ret			; $5d4a

@clearTile:
	ld a,$02		; $5d4b
	ld (de),a		; $5d4d
	set 2,d			; $5d4e
	dec a			; $5d50
	ld (de),a		; $5d51
	ld a,$20		; $5d52
	call addAToDe		; $5d54
	ld a,$01		; $5d57
	ld (de),a		; $5d59
	res 2,d			; $5d5a
	inc a			; $5d5c
	ld (de),a		; $5d5d
	pop de			; $5d5e
	ret			; $5d5f

;;
; Writes tile index to (de) and (de+$20).
; Writes tile attribute to (de+$400), (de+$420).
; @param b Tile attribute
; @param c Tile index
; @addr{5d60}
@writeTileHlpr:
	ld a,c			; $5d60
	ld (de),a		; $5d61
	set 2,d			; $5d62
	ld a,b			; $5d64
	ld (de),a		; $5d65
	ld a,$20		; $5d66
	call addAToDe		; $5d68
	ld a,b			; $5d6b
	ld (de),a		; $5d6c
	res 2,d			; $5d6d
	ld a,c			; $5d6f
	inc a			; $5d70
	ld (de),a		; $5d71
	ret			; $5d72

