	ldi a,(hl)		; $5ef9
	or a			; $5efa
	ret z			; $5efb

	ld b,a			; $5efc

	; de = destination
	ldi a,(hl)		; $5efd
	ld e,a			; $5efe
	ldi a,(hl)		; $5eff
