; @param[out]	cflag	Set on failure (no tree exists for this room)
; @addr{66c6}
_getTreeWarpDataForRoom:
	ld c,a			; $66c6
	call _getWarpTreeData		; $66c7
--
	ldi a,(hl)		; $66ca
	or a			; $66cb
	scf			; $66cc
	ret z			; $66cd
	cp c			; $66ce
	ret z			; $66cf
	inc hl			; $66d0
	inc hl			; $66d1
