	and $80			; $7b46
	ret nz			; $7b48
	ld hl,@rectToDraw		; $7b49
	jp drawRectangleToVramTiles		; $7b4c

@rectToDraw:
	.dw w3VramTiles+$0c
	.db $04 $06

	.db $26 $0c $27 $0c $26 $0c $27 $0c
	.db $26 $0c $27 $0c $26 $0c $27 $0c
	.db $26 $0c $27 $0c $26 $0c $27 $0c
	.db $26 $0c $5e $0c $4c $0c $4c $2c
	.db $5e $2c $27 $0c $26 $0c $3b $0c
	.db $5c $0c $5c $2c $3b $2c $27 $0c

