	ld b,a			; $7d72
	ldi a,(hl)		; $7d73
	ld c,a			; $7d74
	ldi a,(hl)		; $7d75
	ld e,a			; $7d76
	ldi a,(hl)		; $7d77
	ld d,a			; $7d78
	ldi a,(hl)		; $7d79
	ld h,(hl)		; $7d7a
	ld l,a			; $7d7b

@nextRow:
	push bc			; $7d7c
--
	ld a,$02		; $7d7d
	ld ($ff00+R_SVBK),a	; $7d7f
	ldi a,(hl)		; $7d81
	ld b,a			; $7d82
	ld a,$03		; $7d83
	ld ($ff00+R_SVBK),a	; $7d85
	ld a,b			; $7d87
	ld (de),a		; $7d88
	inc de			; $7d89
	dec c			; $7d8a
	jr nz,--		; $7d8b
	pop bc			; $7d8d
	ld a,$20		; $7d8e
	sub c			; $7d90
	call addAToDe		; $7d91
	ld a,$20		; $7d94
	sub c			; $7d96
	rst_addAToHl			; $7d97
	dec b			; $7d98
	jr nz,@nextRow		; $7d99

	pop af			; $7d9b
	ld ($ff00+R_SVBK),a	; $7d9c
	ret			; $7d9e

;;
; @param	hl	Pointer to data struct:
