	ld hl,wTmpcbb3		; $4dcb
	ld a,$60		; $4dce
	ldi (hl),a		; $4dd0
	ld a,$09		; $4dd1
	ldi (hl),a ; [wTmpcbb4] = $09

	call _intro_incState		; $4dd4

	ld a,MUS_TITLESCREEN		; $4dd7
