updateAnimationData:
	ld hl,wAnimationCounter1		; $5942
	ld a,(wAnimationState)		; $5945
	bit 0,a			; $5948
	call nz,updateAnimationDataPointer		; $594a
	ld hl,wAnimationCounter2		; $594d
	ld a,(wAnimationState)		; $5950
	bit 1,a			; $5953
