	.dw @swimming ; TILETYPE_WHIRLPOOL

@tiletype_raisableFloor:
	ld a,-3		; $42fe
	ld (wLinkRaisedFloorOffset),a		; $4300

@tileType_normal:
	xor a			; $4303
