	ld l,SpecialObject.adjacentWallsBitset		; $4323
	ld (hl),$ff		; $4325
	ld l,SpecialObject.collisionType		; $4327
	res 7,(hl)		; $4329
	jr @notSwimming		; $432b

@tileType_vines:
	call dropLinkHeldItem		; $432d
	ld a,$ff		; $4330
	ld (wLinkClimbingVine),a		; $4332

@notSwimming:
	xor a			; $4335
