	ret			; $4339

@tileType_crackedFloor:
	ld a,ROCS_RING		; $433a
	call cpActiveRing		; $433c
	jr z,@tileType_normal	; $433f

	; Don't break the floor until Link has stood there for 32 frames
	ld a,(wStandingOnTileCounter)		; $4341
