; @addr{4432}
_linkAdjustGivenAngleInSidescrollingArea:
	ld h,d			; $4432
	ld e,l			; $4433

	ld a,(wAreaFlags)		; $4434
	and AREAFLAG_SIDESCROLL			; $4437
	ret z			; $4439

