	; Get offset from companion's position for tile to check
	ld e,SpecialObject.direction		; $4756
	ld a,(de)		; $4758
	ld hl,@directionOffsets		; $4759
	rst_addDoubleIndex			; $475c
	ldi a,(hl)		; $475d
	ld b,a			; $475e
	ld c,(hl)		; $475f

	call objectGetRelativeTile		; $4760
