; Updates Link's speed and updates his position if he's experiencing knockback.
; @addr{5d5b}
_linkUpdateKnockback:
	ld e,SpecialObject.state		; $5d5b
	ld a,(de)		; $5d5d
	cp LINK_STATE_RESPAWNING			; $5d5e
	jr z,@cancelKnockback	; $5d60

