	rlca			; $5d65
	jr c,@cancelKnockback	; $5d66

	; Set c to the amount to decrement knockback by.
	; $01 normally, $02 if in the air?
	ld c,$01		; $5d68
	or a			; $5d6a
	jr z,+			; $5d6b
	inc c			; $5d6d
+
	ld h,d			; $5d6e
	ld l,SpecialObject.knockbackCounter		; $5d6f
	ld a,(hl)		; $5d71
	or a			; $5d72
	ret z			; $5d73

