	and $0f			; $5ef9
	or l			; $5efb
	ld l,a			; $5efc
	ld h,>wRoomCollisions		; $5efd
	ld a,(hl)		; $5eff
	cp $10			; $5f00
	jr c,@simpleCollision		; $5f02

