	cp $08			; $5f0b
	ld a,b			; $5f0d
	jr nc,+			; $5f0e
	ld a,c			; $5f10
+
	rrca			; $5f11
	and $07			; $5f12
	ld hl,bitTable		; $5f14
	add l			; $5f17
	ld l,a			; $5f18
	ld a,(hl)		; $5f19
	and e			; $5f1a
	ret z			; $5f1b
	scf			; $5f1c
