	ldh a,(<hFF92)	; $6197
	ld hl,_landableTileFromCliffExceptions		; $6199
	call findByteInCollisionTable		; $619c
	jr c,@landHere	; $619f

	; Try the next tile
	ldh a,(<hFF8B)	; $61a1
	inc a			; $61a3
	ldh (<hFF8B),a	; $61a4
	jr @nextTile			; $61a6

