	ldh a,(<hFF8B)	; $61ad
	cp $0b			; $61af
	jr c,+			; $61b1
	ld a,$0b		; $61b3
+
	or a			; $61b5
	ret			; $61b6

@offsets:
	.db $f8 $00 ; DIR_UP
	.db $00 $08 ; DIR_RIGHT
	.db $08 $00 ; DIR_DOWN
	.db $00 $f8 ; DIR_LEFT


