	jp _companionCheckEnableTerrainEffects		; $6d27

@runState:
	ld e,SpecialObject.state		; $6d2a
	ld a,(de)		; $6d2c
	rst_jumpTable			; $6d2d
	.dw _rickyState0
	.dw _rickyState1
	.dw _rickyState2
	.dw _rickyState3
	.dw _rickyState4
	.dw _rickyState5
	.dw _rickyState6
