	ld hl,w1Link.angle		; $7804
	ldd a,(hl)		; $7807
	bit 7,a			; $7808
	ret nz			; $780a
	bit 2,a			; $780b
	jr nz,@diagonalMovement			; $780d

	or d			; $780f
	ret			; $7810

@diagonalMovement:
	; Calculate the other direction being moved in
	add a			; $7811
	ld b,a			; $7812
	ldi a,(hl) ; a = [direction]
	swap a			; $7814
	srl a			; $7816
	xor b			; $7818
	add a			; $7819
	swap a			; $781a
	and $03			; $781c

