	bit 6,b			; $411e
	jr z,+			; $4120

	ld a,TREASURE_BRACELET		; $4122
	call checkTreasureObtained		; $4124
	ld a,$03		; $4127
	jp nc,showInfoTextForTile		; $4129
+
	; Bit 7 of parameter: if unset, the block can only be pushed one way (bits 4-5)
	bit 7,b			; $412c
	jr nz,++		; $412e

	ld a,b			; $4130
	swap a			; $4131
	and $03			; $4133
	ld l,a			; $4135
	ld a,(wLinkPushingDirection)		; $4136
	cp l			; $4139
	jr nz,@end		; $413a
