	cp TILEINDEX_GRAVE_HIDING_DOOR			; $416c
	jr nz,@end		; $416e
	ld a,(wAreaFlags)		; $4170
	and AREAFLAG_OUTDOORS			; $4173
	jr z,@end			; $4175

