;;
; Gets size, address of graphics to load.
; Also sets w1Link.oamDataAddress.
;
; @param	a	Index of graphics to load
; @param[out]	b	Size of graphics
; @param[out]	c	Bank of graphics
; @param[out]	hl	Address of graphics
; @param[out]	zflag	Set if there are no graphics to load.
; @addr{4516}
_getSpecialObjectGraphicsFrame:
	ld c,a			; $4516
	ld b,$00		; $4517
	ld d,h			; $4519
	ld l,<w1Link.id		; $451a
	ld a,(hl)		; $451c
	ld e,a			; $451d
	ld hl,specialObjectGraphicsTable		; $451e
	rst_addDoubleIndex			; $4521
	ldi a,(hl)		; $4522
	ld h,(hl)		; $4523
	ld l,a			; $4524
	add hl,bc		; $4525
	add hl,bc		; $4526
	add hl,bc		; $4527

	; Byte 0
	ldi a,(hl)		; $4528
	push hl			; $4529
	add a			; $452a
	ld c,a			; $452b
	ld a,e			; $452c
	ld hl,specialObjectOamDataTable		; $452d
	rst_addDoubleIndex			; $4530
	ldi a,(hl)		; $4531
	ld h,(hl)		; $4532
	ld l,a			; $4533
	add hl,bc		; $4534
	ld e,<w1Link.oamDataAddress		; $4535
	ldi a,(hl)		; $4537
	ld (de),a		; $4538
	inc e			; $4539
	ldi a,(hl)		; $453a
	and $3f			; $453b
	ld (de),a		; $453d

	; Bytes 1-2: address of graphics
	pop hl			; $453e
	ldi a,(hl)		; $453f
	ld h,(hl)		; $4540
	ld l,a			; $4541
	or h			; $4542
	ret z			; $4543

	; Bit 0: bank select
	ld a,l			; $4544
	and $01			; $4545
	add :gfx_link		; $4547
	ld c,a			; $4549

	; Bits 1-4: size (divided by 16)
	ld a,l			; $454a
	and $1e			; $454b
	dec a			; $454d
	ld b,a			; $454e

	; Clear bit 4 (bits 0-3 will be ignored by dma)
	res 4,l			; $454f

	; Clear zero flag
	or d			; $4551
	ret			; $4552

;;
; @param[out]	b	Frame index to use (not accounting for direction)
;
; @addr{4553}
_func_4553:
	ld a,(w1Link.id)		; $4553
	or a			; $4556
	jr z,+			; $4557

	ld a,(w1Link.var31)		; $4559
