;;
; Reads w1Link.damageToApply, and reduces Link's health based on this value.
; Also triggers the potion if necessary, and accounts for the protection ring.
; @param d Link object
; @addr{46bb}
linkApplyDamage:
	ld h,d			; $46bb
	ld l,SpecialObject.damageToApply		; $46bc
	ld a,(hl)		; $46be
	ld (hl),$00		; $46bf
	or a			; $46c1
	jr z,++			; $46c2

	; Protection ring does fixed damage on each hit
	ld b,a			; $46c4
	ld a,PROTECTION_RING		; $46c5
	call cpActiveRing		; $46c7
	jr nz,+			; $46ca
	ld b,$f8		; $46cc
+
	; Add the value to w1Link.health. His "real" health variable is at wLinkHealth, so
	; this appears to be used as part of the calculation to reduce that.
	ld l,SpecialObject.health		; $46ce
	ld a,(hl)		; $46d0
	add b			; $46d1
	ld (hl),a		; $46d2
++
	ld l,SpecialObject.var2a		; $46d3
	ld a,(hl)		; $46d5
	or a			; $46d6
	jr z,+			; $46d7

	; Steadfast ring halves knockback
	ld a,STEADFAST_RING		; $46d9
	call cpActiveRing		; $46db
	jr nz,+			; $46de
	ld l,SpecialObject.knockbackCounter		; $46e0
	srl (hl)		; $46e2
+
	ld hl,wLinkHealth		; $46e4
	ld e,SpecialObject.health		; $46e7

	; Make sure that w1Link.health is negative. At this point, w1Link.health is
	; actually being used similarly to w1Link.damageToApply, and doesn't reflect his
	; actual health.
	ld a,(de)		; $46e9
	bit 7,a			; $46ea
	jr z,++			; $46ec

	; Apply the damage (finally update wLinkHealth)
	ld a,(de)		; $46ee
--
	dec (hl)		; $46ef
	add $02			; $46f0
	jr nc,--		; $46f2

	ld (de),a		; $46f4
++
	; Jump if [wLinkHealth] > 0
	ld a,(hl)		; $46f5
	dec a			; $46f6
	rlca			; $46f7
	jr nc,++		; $46f8

; Link's health has reached 0.

	; Replenish health if Link has a potion.
	ld a,TREASURE_POTION		; $46fa
	call checkTreasureObtained		; $46fc
	jr nc,@noPotion			; $46ff

	; [wLinkHealth] = [wLinkMaxHealth]
	ld hl,wLinkMaxHealth		; $4701
	ldd a,(hl)		; $4704
	ld (hl),a		; $4705

	; Set w1Link.health to $01 (again, this doesn't represent his actual health)
	ld a,$01		; $4706
	ld (de),a		; $4708

	ld a,TREASURE_POTION		; $4709
	call loseTreasure		; $470b
	jr ++			; $470e

; Link is dead, and has no potion.
@noPotion:
	; Clear wLinkHealth and w1Link.health
	xor a			; $4710
	ld (de),a		; $4711
	ld (hl),a		; $4712
	ld (wUsingShield),a		; $4713

	ld e,SpecialObject.state		; $4716
	ld a,(de)		; $4718
	cp LINK_STATE_GRABBED			; $4719
	jr z,++			; $471b

	ld a,$ff		; $471d
	ld (wLinkDeathTrigger),a		; $471f
	call clearAllParentItems		; $4722
++
	; Decrement the stun counter every other frame?
	ld a,(wFrameCounter)		; $4725
	rrca			; $4728
	jr nc,++			; $4729

	ld e,SpecialObject.stunCounter		; $472b
	ld a,(de)		; $472d
	or a			; $472e
	jr z,++			; $472f

	dec a			; $4731
	ld (de),a		; $4732
++
	ret			; $4733

;;
; This is always called from the tryToBreakTile function in bank 0.
;
; @param	bc	Position of tile to try to break
; @param	ff8f	Type of breakage (digging, sword slashing). Set bit 7 if no tiles
;			should be modified, in that case this function will only check if
;			it can be broken.
; @param[out]	hFF8D	4th parameter from "_breakableTileModes"
; @param[out]	hFF8E	The interaction ID to create when the tile is destroyed
; @param[out]	hFF92	Former tile index
; @param[out]	hFF93	Tile position
; @param[out]	cflag	Set if the tile was broken (or can be broken).
;
; Internal variables:
;  ff8d-ff8e: values read from _breakableTileModes
;  ff90: Y
;  ff91: X
;
; @addr{4734}
tryToBreakTile_body:
	ld a,b			; $4734
	and $f0			; $4735
	or $08			; $4737
	ldh (<hFF90),a	; $4739
	ld a,c			; $473b
	and $f0			; $473c
	or $08			; $473e
	ldh (<hFF91),a	; $4740

	call getTileAtPosition		; $4742
	ldh (<hFF92),a	; $4745
	ld e,a			; $4747
	ld a,l			; $4748
	ldh (<hFF93),a	; $4749

	ld hl,_breakableTileCollisionTable	; $474b
	call lookupCollisionTable_paramE		; $474e
	ret nc			; $4751

	; hl = _breakableTileModes + a*5
	ld e,a			; $4752
	add a			; $4753
	ld hl,_breakableTileModes	; $4754
	rst_addDoubleIndex			; $4757
	ld a,e			; $4758
	rst_addAToHl			; $4759

	ldh a,(<hFF8F)	; $475a
	ld e,a			; $475c
	and $1f			; $475d
	call checkFlag		; $475f
	ret z			; $4762
	rl e			; $4763
	ret c			; $4765

	inc hl			; $4766
	inc hl			; $4767
	ldi a,(hl)		; $4768
	swap a			; $4769
	and $0f			; $476b
	ldh (<hFF8D),a	; $476d
	ldi a,(hl)		; $476f
	ldh (<hFF8E),a	; $4770
	push de			; $4772
	ld a,(hl)		; $4773
	or a			; $4774
	jr z,@doneSettingTile	; $4775

	ldh a,(<hFF92)	; $4777
	cp TILEINDEX_SWITCH_DIAMOND	; $4779
	jr z,@useOriginalLayout	; $477b

	ld a,(wActiveCollisions)		; $477d
	cp $02			; $4780
	jr z,@activeCollisions1Or2	; $4782

	cp $01			; $4784
	jr nz,@useGivenValue	; $4786

@activeCollisions1Or2:
	; Check value $10 (a kind of pot). What's the significance?
	ldh a,(<hFF92)	; $4788
	cp TILEINDEX_MOVING_POT			; $478a
	jr nz,@useGivenValue	; $478c

@useOriginalLayout:
	; Check the original layout of the room, set the tile to its original
	; value if it was non-solid
	ldh a,(<hFF93)	; $478e
	push hl			; $4790
	call getTileIndexFromRoomLayoutBuffer		; $4791
	pop hl			; $4794
	jr nc,@setTile		; $4795

@useGivenValue:
	ldh a,(<hFF93)	; $4797
	ld c,a			; $4799
	ld b,(hl)		; $479a
	call setTileInRoomLayoutBuffer		; $479b
	ld a,(hl)		; $479e
@setTile:
	call setTile		; $479f
@doneSettingTile:
	ldh a,(<hFF92)	; $47a2
	cp TILEINDEX_SOMARIA_BLOCK	; $47a4
	jr z,@somariaBlock	; $47a6

	cp TILEINDEX_SIGN	; $47a8
	ld hl,wTotalSignsDestroyed	; $47aa
	call z,incHlRefWithCap		; $47ad

	ldh a,(<hFF8E)	; $47b0
	rlca			; $47b2
	ldh a,(<hFF92)	; $47b3
	call c,updateRoomFlagsForBrokenTile		; $47b5

	ldh a,(<hFF8E)	; $47b8
	bit 6,a			; $47ba
	ld a,SND_SOLVEPUZZLE		; $47bc
	call nz,playSound		; $47be

	ld hl,$ccaa		; $47c1
	ldh a,(<hFF93)	; $47c4
	cp (hl)			; $47c6
	jr nz,+			; $47c7

	ld (hl),$ff		; $47c9
	jr ++			; $47cb
+
	ldh a,(<hFF8D)	; $47cd
	or a			; $47cf
	call nz,func_483d		; $47d0
++
	ldh a,(<hFF8F)	; $47d3
	or a			; $47d5
	jr z,@done		; $47d6
	cp $0c			; $47d8
	jr z,@done		; $47da
	cp $08			; $47dc
	jr z,@done		; $47de
	cp $12			; $47e0
	ldh a,(<hFF8E)	; $47e2
	call nz,_makeInteractionForBreakableTile		; $47e4
@done:
	pop de			; $47e7
	scf			; $47e8
	ret			; $47e9

@somariaBlock:
	ld c,ITEMID_18		; $47ea
	call findItemWithID		; $47ec
	jr nz,@done		; $47ef

	call @deleteSomariaBlock		; $47f1
	call findItemWithID_startingAfterH		; $47f4
	jr nz,@done		; $47f7

	call @deleteSomariaBlock		; $47f9
	jr @done		; $47fc

;;
; @param	h	Somaria block to slate for deletion
; @addr{47fe}
@deleteSomariaBlock:
	ld l,Item.state		; $47fe
	ld a,(hl)		; $4800
	cp $03			; $4801
	ret nz			; $4803

	ld l,Item.var2f		; $4804
	set 5,(hl)		; $4806
	ret			; $4808

;;
; Makes an interaction for a breakable tile at the item's location.
; The effect that will be made is based on the Item.var03 variable.
; @addr{4809}
itemMakeInteractionForBreakableTile:
	ld h,d			; $4809
	ld l,Item.yh		; $480a
	ldi a,(hl)		; $480c
	ldh (<hFF90),a	; $480d
	inc l			; $480f
	ldi a,(hl)		; $4810
	ldh (<hFF91),a	; $4811
	ld l,Item.var03		; $4813
	ld a,(hl)		; $4815
;;
; @addr{4816}
_makeInteractionForBreakableTile:
	and $1f			; $4816
	cp $1f			; $4818
	ret z			; $481a

	ld c,a			; $481b
	call getFreeInteractionSlot		; $481c
	ret nz			; $481f

	ld a,c			; $4820
	and $0f			; $4821
	ldi (hl),a		; $4823
	ld a,c			; $4824
	and $10			; $4825
	swap a			; $4827
	ldi (hl),a		; $4829
	ld a,(w1Link.direction)		; $482a
	ld l,Interaction.direction		; $482d
	ldi (hl),a		; $482f
	swap a			; $4830
	rrca			; $4832
	ldi (hl),a		; $4833
	inc l			; $4834
	ldh a,(<hFF90)	; $4835
	ldi (hl),a		; $4837
	inc l			; $4838
	ldh a,(<hFF91)	; $4839
	ldi (hl),a		; $483b
	ret			; $483c

;;
; @addr{483d}
func_483d:
	push hl			; $483d
	call func_16eb		; $483e
	jr z,@done		; $4841

	call getFreePartSlot		; $4843
	jr nz,@done		; $4846

	ld (hl),PARTID_01	; $4848
	inc l			; $484a
	ld (hl),c		; $484b
	ld l,Part.yh		; $484c
	ldh a,(<hFF90)	; $484e
	ldi (hl),a		; $4850
	inc l			; $4851
	ldh a,(<hFF91)	; $4852
	ld (hl),a		; $4854
	ld a,(w1Link.direction)		; $4855
	swap a			; $4858
	rrca			; $485a
	ld l,Part.angle	; $485b
	ld (hl),a		; $485d
	ld l,Part.var03		; $485e
	ld a,c			; $4860
	cp $0f			; $4861
	jr nz,+			; $4863
	ld (hl),$02		; $4865
+
	ldh a,(<hFF8F)	; $4867
	cp $06			; $4869
	jr nz,@done		; $486b
	inc (hl)		; $486d
@done:
	pop hl			; $486e
	ret			; $486f

;;
; Indirectly calls a few functions. Used for code outside of this bank.
; @param c Index of the function to run
; @addr{4870}
functionCaller:
	ld a,c			; $4870
	rst_jumpTable			; $4871
	.dw _clearAllParentItems
	.dw _updateParentItemButtonAssignment_body
	.dw checkUseItems

;;
; Clears all variables related to items being used?
; @addr{4878}
_clearAllParentItems:
	push de			; $4878
	ld d,>w1ParentItem2		; $4879
--
	call _clearParentItem		; $487b
	inc d			; $487e
	ld a,d			; $487f
	cp WEAPON_ITEM_INDEX			; $4880
	jr c,--			; $4882

	xor a			; $4884
	ld (wUsingShield),a		; $4885
	ld ($cc63),a		; $4888
	ld ($cc5e),a		; $488b
	pop de			; $488e
	ret			; $488f

;;
; Called from "updateParentItemButtonAssignment" in bank 0.
;
; Updates var03 of a parent item to correspond to the equipped A or B button item. This is
; called after closing a menu (since button assignments may be changed).
;
; @addr{4890}
_updateParentItemButtonAssignment_body:
	ld h,>w1ParentItem2		; $4890
@itemLoop:
	ld l,Item.enabled		; $4892
	ldi a,(hl)		; $4894
	or a			; $4895
	jr z,+			; $4896

	; Compare Item.id to the B button item
	ld a,(wInventoryB)		; $4898
	cp (hl)			; $489b
	ld a,BTN_B		; $489c
	jr z,++			; $489e

	; Compare Item.id to the A button item
	ld a,(wInventoryA)		; $48a0
	cp (hl)			; $48a3
	ld a,BTN_A		; $48a4
	jr z,++			; $48a6
+
	; Doesn't correspond to A or B
	xor a			; $48a8
++
	ld l,Item.var03		; $48a9
	ld (hl),a		; $48ab
	inc h			; $48ac
	ld a,h			; $48ad
	cp WEAPON_ITEM_INDEX			; $48ae
	jr c,@itemLoop		; $48b0
	ret			; $48b2

;;
; Use items if the appropriate buttons are pressed along with other conditions.
;
; @addr{48b3}
checkUseItems:
	xor a			; $48b3
	ld (wUsingShield),a		; $48b4
	ld hl,wSwordDisabledCounter		; $48b7
	ld a,(hl)		; $48ba
	or a			; $48bb
	jr z,+			; $48bc
	dec (hl)		; $48be
+
	ld hl,wLinkUsingItem1		; $48bf
	ld a,(hl)		; $48c2
	and $0f			; $48c3
	ld (hl),a		; $48c5
	ld a,($cc63)		; $48c6
	rlca			; $48c9
	jr c,@itemsDisabled	; $48ca

	ld a,(wInShop)		; $48cc
	or a			; $48cf
	jp nz,_checkShopInput		; $48d0

	ld a,($cc95)		; $48d3
	ld b,a			; $48d6
	ld a,(wLinkInAir)		; $48d7
	or b			; $48da
	rlca			; $48db
	jr c,@updateParentItems	; $48dc

	ld a,($ccd8)		; $48de
	ld b,a			; $48e1
	ld a,(wLinkGrabState)		; $48e2
	or b			; $48e5
	jr nz,@updateParentItems	; $48e6

	ld a,(wLinkClimbingVine)		; $48e8
	inc a			; $48eb
	jr z,@updateParentItems	; $48ec

	ld a,(wAreaFlags)		; $48ee
	bit AREAFLAG_BIT_SIDESCROLL,a			; $48f1
	jr nz,@sidescroll	; $48f3

	bit AREAFLAG_BIT_UNDERWATER,a			; $48f5
	jr z,@normal		; $48f7

	; When underwater, only check the A button
@underwater:
	ldde BTN_A, <wInventoryA		; $48f9
	call _checkItemUsed		; $48fc
	jr @updateParentItems		; $48ff

	; When in the overworld, only check buttons if not swimming
@normal:
	ld a,(wLinkSwimmingState)		; $4901
	or a			; $4904
	jr z,@checkAB	; $4905
	jr @updateParentItems		; $4907

	; If swimming in a sidescrolling area, only check the B button?
@sidescroll:
	ld a,(wLinkSwimmingState)		; $4909
	or a			; $490c
	jr z,@checkAB	; $490d

	ld hl,w1Link.var2f		; $490f
	bit 7,(hl)		; $4912
	jr z,@checkB	; $4914

@checkAB:
	ldde BTN_A, <wInventoryA		; $4916
	call _checkItemUsed		; $4919
@checkB:
	ldde BTN_B, <wInventoryB		; $491c
	call _checkItemUsed		; $491f

	; Update all "parent items"
@updateParentItems:
	ld de,w1ParentItem2		; $4922
@parentItemLoop:
	ld e,Object.enabled		; $4925
	ld a,(de)		; $4927
	or a			; $4928
	call nz,_parentItemUpdate		; $4929
	inc d			; $492c
	ld a,d			; $492d
	cp FIRST_ITEM_INDEX			; $492e
	jr c,@parentItemLoop		; $4930

	lda <w1Link			; $4932
	ldh (<hActiveObjectType),a	; $4933
	ld d,>w1Link		; $4935
	ld a,d			; $4937
	ldh (<hActiveObject),a	; $4938
	ret			; $493a

@itemsDisabled:
	cp $ff			; $493b
	jr nz,@updateParentItems	; $493d

	; If [$cc63] == $ff, force Link to do a sword spin animation?

	call _clearAllParentItems		; $493f

	ld hl,w1ParentItem2		; $4942
	ldde $ff, ITEMID_SWORD		; $4945
	ld c,$f1		; $4948
	call _initializeParentItem		; $494a

	ld a,$80		; $494d
	ld ($cc63),a		; $494f
	jr @updateParentItems		; $4952

;;
; Creates a parent item object if an item is used.
;
; @param	d	Bitmask for button to check
; @param	e	Low byte of inventory item address to check
; @addr{4954}
_checkItemUsed:
	ld h,>wInventoryB		; $4954
	ld l,e			; $4956
	ld a,(hl)		; $4957
	or a			; $4958
	jr nz,@checkItem	; $4959

	; Nothing equipped; return unless Link is wearing a punching ring
	ld a,(wActiveRing)		; $495b
	cp EXPERTS_RING			; $495e
	jr z,@punch		; $4960
	cp FIST_RING			; $4962
	ret nz			; $4964

	; Punch if nothing equipped
@punch:
	ld l,<wInventoryB		; $4965
	ldi a,(hl)		; $4967
	or (hl)			; $4968
	ret nz			; $4969

	ld a,ITEMID_PUNCH		; $496a

@checkItem:
	; Item IDs $20 and above can't be used directly as items?
	cp NUM_INVENTORY_ITEMS			; $496c
	ret nc			; $496e

	ld e,a			; $496f
	ld hl,_itemUsageParameterTable		; $4970
	rst_addDoubleIndex			; $4973
	ldi a,(hl)		; $4974
	ld c,a			; $4975

	; Check if button is pressed (reads wGameKeysPressed or wGameKeysJustPressed)
	ld l,(hl)		; $4976
	ld h,>wGameKeysPressed		; $4977
	ld a,(hl)		; $4979
	and d			; $497a
	ret z			; $497b

	call _chooseParentItemSlot		; $497c
	ret nz			; $497f

;;
; Initialize a parent item?
;
; @param	c	Upper nibble for Item.enabled
; @param	d	Button pressed
; @param	e	Item.id
; @addr{4980}
_initializeParentItem:
	ld a,c			; $4980
	and $f0			; $4981
	inc a			; $4983
	ld l,Item.enabled		; $4984
	ldi (hl),a		; $4986

	; Set Item.id
	ld (hl),e		; $4987

	; Set Item.var03 to the button pressed
	inc l			; $4988
	inc l			; $4989
	ld (hl),d		; $498a
	ret			; $498b

;;
; Figure out which parent item slot an item should use (if at all).
;
; @param	c	Byte read from _itemUsageParameterTable
; @param	e	Item.id (returned unmodified)
;
; @param[out]	c	Value for upper nibble of Item.enabled
; @param[out]	hl	Parent item slot to write to
; @param[out]	zflag	Set if valid values for 'c' and 'hl' are returned.
; @addr{498c
_chooseParentItemSlot:
	ld a,c			; $498c
	and $0f			; $498d
	rst_jumpTable			; $498f
	.dw @thing0
	.dw @thing1
	.dw @thing2
	.dw @thing3
	.dw @thing4
	.dw @thing5

;;
; @addr{499c}
@thing4:
	ld a,(w1ParentItem2.enabled)		; $499c
	or a			; $499f
	ret nz			; $49a0

;;
; Only one of the item can exist at a time.
;
; @addr{49a1}
@thing2:
	ld hl,w1ParentItem3.id		; $49a1
	ld a,e			; $49a4
	cp (hl)			; $49a5
	jr z,@thing0	; $49a6

	; Check w1ParentItem4.id
	inc h			; $49a8
	cp (hl)			; $49a9
	jr z,@thing0	; $49aa

;;
; @addr{49ac}
@thing1:
	ld hl,w1ParentItem3.enabled		; $49ac
	ld a,(hl)		; $49af
	or a			; $49b0
	ret z			; $49b1

	; Check w1ParentItem4.enabled
	inc h			; $49b2
	ld a,(hl)		; $49b3
	or a			; $49b4
	ret			; $49b5

;;
; Sword, cane, bombs, shovel, bracelet, feather...
;
; @addr{49b6}
@thing3:
	; If w1ParentItem2 is already in use, continue only if this object's priority is
