--
	ld a,(hl)		; $5436
	or a			; $5437
	jr nz,+			; $5438
	inc b			; $543a
+
	inc h			; $543b
	ld a,h			; $543c
	cp LAST_DYNAMIC_ITEM_INDEX+1			; $543d
	jr c,--			; $543f
	ld a,b			; $5441
	or a			; $5442
	ret			; $5443

