	ld l,Interaction.angle		; $57d3
	ld (hl),b		; $57d5

	; Set position (this interaction stuffs both X and Y in the yh variable)
	ld l,Interaction.yh		; $57d6
	ldh a,(<hFF8B)	; $57d8
	ld (hl),a		; $57da

