	.db <wObtainedTreasureFlags,		(1<<TREASURE_PUNCH) | (1<<TREASURE_SWORD)
	.db <wPirateShipY			$58
	.db <wPirateShipX			$78
	.db $00

; This string is different in ages and seasons.
; @addr{41c9}
_saveVerificationString:
	.ASC "Z21216-0"

