	and AREAFLAG_SIDESCROLL			; $4b44
	jr z,+			; $4b46

	ld b,INTERACID_LAVASPLASH		; $4b48
	bit 2,(hl)		; $4b4a
	jr nz,@createSplash		; $4b4c

	ld b,INTERACID_SPLASH		; $4b4e
