; Create a staircase when all enemies are killed
@subid04:
	call returnIfScrollMode01Unset		; $4385

	call getThisRoomFlags		; $4388
	bit ROOMFLAG_BIT_KEYBLOCK,a			; $438b
	jp nz,interactionDelete		; $438d

	ld a,(wNumEnemies)		; $4390
	or a			; $4393
	ret nz			; $4394

