	ld c,a			; $43ea
	ld b,>wRoomLayout		; $43eb
	ld a,(bc)		; $43ed
	inc l			; $43ee
	ld (hl),a ; [var19] = tile at position
	ld a,TILEINDEX_PUSHABLE_BLOCK		; $43f0
