	ld e,Interaction.subid		; $43fc
	ld a,(de)		; $43fe
	cp b			; $43ff
	ret c			; $4400

	ld e,Interaction.state		; $4401
	ld a,$02		; $4403
	ld (de),a		; $4405

	ld e,Interaction.var18		; $4406
	ld a,(de)		; $4408
	ld c,a			; $4409
	inc e			; $440a
	ld a,(de)		; $440b
	ld b,>wRoomLayout		; $440c
	ld (bc),a		; $440e
